import React, {useEffect, useState} from "react";
import "./style.css";
import { useHistory } from "react-router-dom";
import PositionCarouselDemo from "../../components/slider";
import { Row, Col } from "antd";
import Watch from "../../assets/product4.jpg";
import Blog from "../../assets/blog.jpg";
import Modaal from '../../components/modal'
import NavBar from '../../components/navbar'
function Landing() {
  const [login,setLogin]=useState(true)
  const [logout,setLogout]=useState(false)
  const cards=[{
    productName:'Himiscus Flower',
    price: '$7.00'
  },{
    productName:'Dimiscus Flower',
    price: '$8.00'
  },{
    productName:'Chimiscus Flower',
    price: '$9.00'
  },{
    productName:'Chimiscus Flower',
    price: '$9.00'
  }]

  useEffect(()=>{
   const userId= localStorage.getItem('ID')
   if(userId===''){
     setLogin(true)
   }else{
     setLogout(true)
     setLogin(false)
   }
  },login,logout)
  let history = useHistory();
  return (
    <>
      <div className="container">
        <div className="navMain">
          <div className="logo">
            <h1 style={{ color: "white" }}>FacMart</h1>
          </div>
          <div className="headerLabel">
            <p style={{ color: "white", cursor: "pointer" }}>Teas</p>
          </div>
          <div className="headerLabel">
            <p style={{ color: "white", cursor: "pointer" }}>Extras</p>
          </div>
          <div className="headerLabel">
            <p style={{ color: "white", cursor: "pointer" }}>About</p>
          </div>
          <div className="headerLabel">
            <p style={{ color: "white", cursor: "pointer" }}>Contact</p>
          </div>
          <div className="headerLabel">
       { login &&    <p
              onClick={() => {
                history.push("/login");
              }}
              style={{ color: "white", cursor: "pointer", fontSize: "25px" }}
            >
              Log In
            </p> }

      { logout &&      <p
              onClick={() => {
                localStorage.removeItem('ID')
              }}
              style={{ color: "white", cursor: "pointer", fontSize: "25px" }}
            >
              Log Out
            </p>  }

          </div>
        </div>
        

        <div className="cent">
          <p className="centText">It's Always a Tea Time</p>
        </div>
        <div>
          <button className="btncent" style={{ color: "white" }}>
            Shop Now
          </button>
        </div>
      </div>
      {/* container finish */}

      <div className="middle">
        <div className="box"></div>
        <div>
          <p id="text">New Arrivals</p>
        </div>
        <div className="box"></div>
      </div>

      <div className="check">
        <p>hello! We have all types of fresh and organic teas, Check it out</p>
      </div>

      {/* cards row starts here */}
      <Row
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          marginTop: "7rem",
        }}
      >
        {cards.map((item)=>{
          return(
            <Col span={6}

            onClick={()=>{history.push('/productview')}}
            className='cards'
            style={{ border: "1px solid grey",backgroundColor:'#e3e3e3' }}>
              <img src={Watch} height="200px" width="250px" />
              <h3>{item.productName}</h3>
              <h4>{item.price}</h4>
              
            </Col>

          )
        })}
       
      </Row>
      {/* cards row ends here */}
     

    
      <Row
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "4rem",
        }}
      >
        <Col>
          <button
            style={{
              backgroundColor: "black",
              color: "white",
              height: "2.5rem",
              width: "150px",
              border: "1px solid grey",
            }}
          >
            Shop All
          </button>
        </Col>
      </Row>
      <Row className="blogs">
        <Col>
          <h1>Read Our Blog</h1>
        </Col>
      </Row>
      <Row
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: "#1f1f29",
          height: "30rem",
        }}
      >
        <Col>
          <h3 style={{ color: "white" }}>
            hellosdjsdjklsjvklsjvkljkcvjaskljlsdj
          </h3>
        </Col>
      </Row>
      <Row
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          backgroundColor: "#ededed",
          height: "25rem",
        }}
      >
        <Col>
          <h1>Get to know bloom's tea is Better.</h1>
          <p>Shop</p>
          <p>Extras</p>
          <p>About</p>
          <p>Contact</p>
          <Col style={{ marginTop: "5rem" }}>
            <h4>
              <u>Visit Stores</u>
            </h4>
            <p>Customer Service: 9234455</p>
          </Col>
        </Col>
        <Col>
          <Col style={{ borderBottom: "1px solid grey" }}>
            <h2>help</h2>
          </Col>
          <p>FAQ</p>
          <p>Shipping & Returns</p>
          <p>Store Policy</p>
          <p>Payment Methods</p>
          <Col style={{ marginTop: "5rem" }}>
            <input type="text" style={{ height: "2rem", width: "300px" }} />
            <button
              style={{
                backgroundColor: "black",
                color: "white",
                height: "2.5rem",
                width: "150px",
                border: "1px solid grey",
              }}
            >
              Subscribe
            </button>
          </Col>
        </Col>
        <Col>
          <Col style={{ borderBottom: "1px solid grey" }}>
            <h2>Follow Us</h2>
          </Col>
          <p>Facebook</p>
          <p>Instagram</p>
          <p>LinkedIn</p>
        </Col>
      </Row>

      <Row
        style={{
          backgroundColor: "#ededed",
